create table alimentzioni (id_alimentazione integer generated by default as identity, alimentazione varchar(255) not null, primary key (id_alimentazione));
create table biciclette (id_freno integer unique, id_sospensione integer unique, id_veicolo integer not null, numero_marce integer not null, primary key (id_veicolo));
create table categorie (id_categoria integer generated by default as identity, categoria varchar(255) not null, primary key (id_categoria));
create table colori (id_colore integer generated by default as identity, colore varchar(255) not null, primary key (id_colore));
create table freni (id_freno integer generated by default as identity, freno varchar(255) not null, primary key (id_freno));
create table macchine (cilindrata integer not null, id_veicolo integer not null, porte integer not null, targa varchar(7) not null unique, primary key (id_veicolo));
create table moto (cilindrata integer not null, id_veicolo integer not null, targa varchar(5) not null unique, primary key (id_veicolo));
create table sospensioni (id_sospensione integer generated by default as identity, sospensione varchar(255) not null, primary key (id_sospensione));
create table veicoli (anno_produzione integer not null, id_alimentazione integer unique, id_categoria integer unique, id_colore integer unique, id_veicolo integer generated by default as identity, numero_ruote integer not null, tipo_veicolo varchar(50) not null, marca varchar(255) not null, modello varchar(255) not null, primary key (id_veicolo));
